mapper.select:
select id, trade_number, batch_number, terminal_number, order_number, agent_id, merchant_number,
merchant_name, pay_channel_id, profit_price, amount, poundage, traded_at, sys_order_id,
account_name, account_number, actual_payment_price, paid_result, paid_code, paid_error_description,
created_at, updated_at, trade_type_id, traded_status, attach_status, customer_id
from trade_records


        trade_type_id=#{type}


        and traded_at>#{start}


        and traded_at>#{end}


        and traded_status=#{status}


order by traded_at desc
limit #{request.offset}, #{request.pageSize}

select count(*)
from trade_records


        trade_type_id=#{type}


        and traded_at>#{start}


        and traded_at>#{end}


        and traded_status=#{status}

select sum(tr.amount) as amounts,sum(p.profits_get) gets,sum(p.profits_pay) pays
from trade_records tr
left join profits p on p.trade_record_id=tr.id


        tr.trade_type_id=#{type}


        and tr.traded_at>#{start}


        and tr.traded_at>#{end}


        and tr.traded_status=#{status}
