mapper.select:
select id, username, password, account_type, name, city_id, types, last_logined_at, 
integral, status, device_code, created_at, updated_at, phone, email
from customers
where types=#{type} 


and (name like #{queryParam} or email like #{queryParam} or phone like #{queryParam})


and status = #{status}

order by created_at desc
limit #{request.offset}, #{request.pageSize}

select count(*)
from customers
where types=#{type}


and (name like #{queryParam} or email like #{queryParam} or phone like #{queryParam})


and status = #{status}
