insert into message_receivers (customer_id, sys_message_id, status
  )
values (#{customerId,jdbcType=INTEGER}, #{sysMessageId,jdbcType=INTEGER}, #{status,jdbcType=TINYINT}
  )

mapper.update:

update message_receivers
set customer_id = #{customerId,jdbcType=INTEGER},
  sys_message_id = #{sysMessageId,jdbcType=INTEGER},
  status = #{status,jdbcType=TINYINT}
where id = #{id,jdbcType=INTEGER}

mapper.select:

select id, customer_id, sys_message_id, status
from message_receivers
where id = #{id,jdbcType=INTEGER}

mapper.insert:




insert into message_receivers (customer_id, sys_message_id, status)

select DISTINCT(c.id), #{sysMessageId},#{STATUS_NO_READ}
    from terminals t inner join customers c on t.customer_id=c.id
    where c.types=#{TYPE_CUSTOMER}


    select DISTINCT(a.customer_id), #{sysMessageId},#{STATUS_NO_READ}
    from terminals t inner join agents a on t.agent_id=a.id


    select f.customer_id, #{sysMessageId},#{STATUS_NO_READ}
    from terminals t inner join goods g on t.good_id=g.id
    inner join factories f on f.id=g.factory_id
    union
    select f.customer_id, #{sysMessageId},#{STATUS_NO_READ}
    from terminals t inner join pay_channels pc on t.pay_channel_id=pc.id
    inner join factories f on f.id=pc.factory_id
