mapper.update:

update opening_requirement_lists
set requirement_setting_id = #{requirementSettingId,jdbcType=INTEGER},
  requirement_type = #{requirementType,jdbcType=BIT},
  opening_requirements_id = #{openingRequirementsId,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:

select id, requirement_setting_id, requirement_type, opening_requirements_id
from opening_requirement_lists
where id = #{id,jdbcType=INTEGER}
