mapper.update:

update pay_channel_billing_cycles
set pay_channel_id = #{payChannelId,jdbcType=INTEGER},
  billing_cycles_id = #{billingCyclesId,jdbcType=INTEGER},
  description = #{description,jdbcType=VARCHAR},
  rate = #{rate,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:

select id, pay_channel_id, billing_cycles_id, description, rate
from pay_channel_billing_cycles
where id = #{id,jdbcType=INTEGER}
